import React, {useState} from "react";

const Used = () => {
    const [text, setText] = useState("");

    function add(){
        setText(useState + 1);
    }

    function sub(){
        setText(useState - 1);
    }

    return (   
        <div>
            <button onClick={add}>+</button>
            {text}
            <button onClick={sub}>-</button>
        </div>

     );
}

export default Used;
